        public DlgKgMasterbudgetAnfragen(int KgBudgetID)
            : this()
        {
            this.kgBudgetID = KgBudgetID;

            qryKgBewilligung.Fill( "SELECT TOP 0 VerantwortlicherMA = '', * FROM KgBewilligung" );
            qryKgBewilligung.Insert();

            qryKgBewilligung["KgBudgetID"] = KgBudgetID;

            SqlQuery qry = DBUtil.OpenSQL(@"
                select	VerantwortlicherMA = USR.LastName + IsNull(', ' + USR.FirstName, ''),
                        USR.UserID
                from    KgBudget BDG
                    inner join FaLeistung LEI on LEI.FaLeistungID = BDG.FaLeistungID
                    inner join XUser      USR on USR.UserID = LEI.UserID
                where   BDG.KgBudgetID = {0}",
                KgBudgetID);

            qryKgBewilligung["VerantwortlicherMA"] = qry["VerantwortlicherMA"];
            qryKgBewilligung["UserID_An"] = qry["UserID"];
        }

