        private void cmdAbbrechen_Click(object sender, System.EventArgs e)
        {
            this.userCancel = true;

            qryKgBewilligung.RowModified = false;
            qryKgBewilligung.DataSet.AcceptChanges();
        }

        private void cmdYes_Click(object sender, System.EventArgs e)
        {
            this.userCancel = false;
            qryKgBewilligung.RowModified = true;
            if (qryKgBewilligung.Post())
                this.DialogResult = DialogResult.OK;
        }

        private void qryKgBewilligung_BeforePost(object sender, System.EventArgs e)
        {
            if (DBUtil.IsEmpty(edtDatum.EditValue))
            {
                throw new KissInfoException("'Beenden am' muss ausgefüllt sein!");
            }

            edtDatum.EditValue = Utils.lastDayOfMonth(edtDatum.DateTime); //Datumskorrektur, falls untermonatig

            if ( this.datum < (DateTime)edtDatum.EditValue )
            {
                throw new KissInfoException("Das gewünschte Abschlussdatum liegt nach dem geplanten Abschlussdatum");
            }
            else if ( mindatum != null && edtDatum.DateTime < (DateTime) mindatum)
            {
                throw new KissInfoException(string.Format("Das Masterbudget kann frühestens auf den {0:d} beendet werden (aufgrund grüner/roter Monatsbudgets)", mindatum));
            }

            qryKgBewilligung["PerDatum"] = edtDatum.EditValue;
            qryKgBewilligung["KgBewilligungCode"] = 9;
            qryKgBewilligung["UserID"] = Session.User.UserID;
            qryKgBewilligung["Datum"] = DateTime.Now;

            this.Datum = Convert.ToDateTime(qryKgBewilligung["PerDatum"]);
        }

