        // ComponentName: qryKgBewilligung
        public DlgKgMasterbudgetVisieren( int KgBudgetID )
            : this()
        {
            SqlQuery qry = DBUtil.OpenSQL(@"
                select Anfragender = USR.LastName + IsNull(', ' + USR.FirstName, ''),
                       UserIDAn = USR.UserID
                from   KgBewilligung BEW
                       left join XUser USR on USR.UserID = BEW.UserID
                where  BEW.KgBudgetID = {0} and
                       BEW.KgBewilligungCode = 3",
                KgBudgetID);

            Datum = DateTime.Now;

            qryKgBewilligung.Fill( "SELECT TOP 0 Anfragender = '', * FROM KgBewilligung" );
            qryKgBewilligung.Insert();

            qryKgBewilligung["KgBudgetID"] = KgBudgetID;

            if (qry.Count > 0)
            {
                qryKgBewilligung["Anfragender"] = qry["Anfragender"];
                edtSender.EditValue = qry["Anfragender"].ToString();
                qryKgBewilligung["UserID_An"] = qry["UserIDAn"];
            }
        }

